if (!items[0].binary?.data) {
  throw new Error('No llega binary.data desde el nodo anterior');
}
items[0].binary.data.mimeType = 'image/jpeg';
items[0].binary.data.fileName = 'receipt.jpg';
return items;

// Format and clean all customer data from merged inputs
// The Merge node combines receipt data + Google Sheets customer data
const data = $input.item.json;

// Receipt data (from Scan Receipt node)
let receiptContent = data.content || data.text || {};

// Parse content if it's a JSON string
if (typeof receiptContent === 'string') {
  try {
    receiptContent = JSON.parse(receiptContent);
  } catch (e) {
    console.log('Could not parse content as JSON:', e);
  }
}

// Extract cashierName from the receipt content
const cashierName = receiptContent.cashierName || data.cashierName || '';

// Extract first name from cashierName for budtender lookup
// e.g., "Brooke W." -> "Brooke", "Sophia" -> "Sophia"
const cashierFirstName = cashierName ? cashierName.split(' ')[0].trim() : '';

// Extract fields from Google Sheets format (from Google Sheets Trigger)
const firstName = (data['Name - First Name'] || '').trim().replace(/[^a-zA-Z\s-']/g, '');
const lastName = (data['Name - Last Name'] || '').trim().replace(/[^a-zA-Z\s-']/g, '');
const phoneNumber = data['Phone Number'] || '';
const email = (data['Email'] || '').trim().toLowerCase();
const store = (data['Store'] || '').trim(); // This is the dispensary name
const submissionId = data['Submission ID'] || '';
const fileUpload = data['File Upload'] || '';
const question = data['Type a question'] || '';
const source = data['source'] || '';

// Format phone number properly - keep original format, just clean it
let formattedPhone = phoneNumber.replace(/\D/g, ''); // Remove all non-numeric characters

// Only add +1 if it's a 10-digit US number
if (formattedPhone.length === 10) {
  formattedPhone = '+1' + formattedPhone;
} else if (formattedPhone.length === 11 && formattedPhone.startsWith('1')) {
  formattedPhone = '+' + formattedPhone;
} else if (formattedPhone.length > 0 && !formattedPhone.startsWith('+')) {
  formattedPhone = '+' + formattedPhone;
}

// Keep original phone number if formatting fails
if (!formattedPhone || formattedPhone.length < 10) {
  formattedPhone = phoneNumber;
}

// Validate email
let cleanEmail = email;
if (email && !email.includes('@')) {
  console.log('Invalid email format:', email);
  cleanEmail = '';
}

return {
  json: {
    // Receipt scan data
    content: receiptContent,
    cashierName: cashierName,
    cashierFirstName: cashierFirstName,
    // Google Sheets customer data
    firstName: firstName,
    lastName: lastName,
    phoneNumber: formattedPhone,
    formattedPhone: formattedPhone,
    email: cleanEmail,
    store: store, // This is the dispensary name
    dispensary: store, // Alias for dispensary
    submissionId: submissionId,
    fileUpload: fileUpload,
    question: question,
    source: source,
    // Keep original data for reference
    originalPhone: phoneNumber,
    originalEmail: email,
    // Debug info
    _debug: {
      hasFirstName: !!firstName,
      hasLastName: !!lastName,
      hasPhone: !!phoneNumber,
      hasEmail: !!email,
      hasStore: !!store,
      hasReceipt: !!receiptContent,
      hasCashierName: !!cashierName,
      allKeys: Object.keys(data)
    }
  }
};
