// Delay 375ms to preserve send order
await new Promise(r => setTimeout(r, 375));
return $input.all();

// Fixed filter for Supabase array response (aligned with v4.001)
console.log('=== Filter Debug v4.101 ===');

const messages = $input.all().map(item => item.json);

const unreadInbound = messages.filter(msg => msg.direction === 'inbound' && msg.status === 'unread');

if (unreadInbound.length === 0) {
  return [];
}

return unreadInbound.map(msg => ({
  json: {
    message_id: msg.id,
    phone_number: msg.phone_number,
    incoming_message: msg.content,
    timestamp: msg.timestamp,
    conversation_history: `[${new Date(msg.timestamp).toLocaleString()}] User: ${msg.content}`,
    total_messages: 1,
    conversation_start: msg.timestamp,
    conversation_duration: 'New conversation'
  }
}));

// Extract AI response and prepare for SMS sending
let aiResponse;
if ($input.item.json.output) {
  aiResponse = $input.item.json.output;
} else if ($input.item.json.text) {
  aiResponse = $input.item.json.text;
} else {
  aiResponse = $input.item.json.response || 'Hello! How can I help you with your dispensary SMS marketing?';
}

// CRITICAL: Get message_id from 'Prepare for AI' node - AI agent doesn't preserve it
const prepareNode = $('Prepare for AI').item.json;
const phoneNumber = prepareNode.phone_number;
const messageId = prepareNode.message_id;
const conversationStage = prepareNode.conversation_stage;

// Log for debugging
console.log('=== MarketBot Response Debug ===');
console.log('Phone:', phoneNumber);
console.log('Message ID:', messageId, 'Type:', typeof messageId);
console.log('Stage:', conversationStage);
console.log('AI Response length:', aiResponse?.length);

if (!messageId) {
  throw new Error('Message ID is missing from Prepare for AI node!');
}

// Track lead qualification
let leadStatus = 'new';
if (conversationStage === 'qualification') {
  leadStatus = 'qualified';
} else if (conversationStage === 'demo') {
  leadStatus = 'demo_scheduled';
}

return {
  json: {
    phone_number: phoneNumber,
    message_id: messageId,
    message: aiResponse,
    original_message_id: messageId,
    ai_full_response: aiResponse,
    conversation_stage: conversationStage,
    lead_status: leadStatus
  }
};

// Sanitize and produce ONE SMS <=150 chars to avoid doubles
const prepare = $('Prepare SMS Response').item.json;
let text = (prepare.message || '')
  .replace(/[\r\n]+/g, ' ')
  .replace(/\s+/g, ' ')
  .replace(/[\*_`~]/g, '')
  .trim();
const limit = 150;
if (text.length > limit) {
  let idx = text.lastIndexOf(' ', limit);
  if (idx === -1) idx = limit;
  text = text.slice(0, idx).trim();
}
return [{ json: { phone_number: prepare.phone_number, content: text } }];
