// Smart conversation processing - only respond to NEW inbound messages
const response = $input.item.json;

if (!response.messages || !Array.isArray(response.messages)) {
  return [];
}

// Group messages by phone number
const messagesByPhone = {};
response.messages.forEach(msg => {
  if (!messagesByPhone[msg.phone_number]) {
    messagesByPhone[msg.phone_number] = [];
  }
  messagesByPhone[msg.phone_number].push(msg);
});

const messagesToProcess = [];

// For each phone number, check if we need to respond
Object.keys(messagesByPhone).forEach(phoneNumber => {
  const messages = messagesByPhone[phoneNumber]
    .sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp)); // Chronological order

  if (messages.length === 0) return;

  const lastMessage = messages[messages.length - 1];

  // Only process if the last message is inbound AND unread (user sent something new)
  if (lastMessage.direction === 'inbound' && lastMessage.status === 'unread') {
    messagesToProcess.push({
      phoneNumber,
      lastMessage,
      conversationHistory: messages
    });
  }
  // If last message is outbound, we already responded - no action needed
});

if (messagesToProcess.length === 0) {
  return [];
}

// Format conversation history with timestamps for AI context
return messagesToProcess.map(({ phoneNumber, lastMessage, conversationHistory }) => {
  const formattedHistory = conversationHistory.map(h => {
    const timestamp = new Date(h.timestamp).toLocaleString();
    const sender = h.direction === 'inbound' ? 'User' : 'AI';
    return `[${timestamp}] ${sender}: ${h.content}`;
  }).join('\n');

  return {
    json: {
      message_id: lastMessage.id,
      phone_number: phoneNumber,
      incoming_message: lastMessage.content,
      timestamp: lastMessage.timestamp,
      conversation_history: formattedHistory,
      total_messages: conversationHistory.length,
      conversation_start: conversationHistory[0]?.timestamp,
      conversation_duration: conversationHistory.length > 1 ? 
        `${Math.round((new Date(conversationHistory[conversationHistory.length-1].timestamp) - new Date(conversationHistory[0].timestamp)) / (1000 * 60 * 60))} hours` : 'New conversation'
    }
  };
});

// Extract AI response and prepare for SMS sending
let aiResponse;
if ($input.item.json.output) {
  aiResponse = $input.item.json.output;
} else if ($input.item.json.text) {
  aiResponse = $input.item.json.text;
} else {
  aiResponse = $input.item.json.response || 'Hello! How can I help you?';
}

// CRITICAL: Get message_id from 'Prepare for AI' node - AI agent doesn't preserve it
const prepareNode = $('Prepare for AI').item.json;
const phoneNumber = prepareNode.phone_number;
const messageId = prepareNode.message_id;

// Log for debugging
console.log('=== Prepare SMS Response Debug ===');
console.log('Phone:', phoneNumber);
console.log('Message ID:', messageId, 'Type:', typeof messageId);
console.log('AI Response length:', aiResponse?.length);

if (!messageId) {
  throw new Error('Message ID is missing from Prepare for AI node!');
}

return {
  json: {
    phone_number: phoneNumber,
    message_id: messageId,
    message: aiResponse,
    original_message_id: messageId,
    ai_full_response: aiResponse
  }
};

// Get the original data from Prepare SMS Response node
const prepareData = $('Prepare SMS Response').item.json;

return {
  json: {
    phone_number: prepareData.phone_number,
    message: prepareData.message
  }
};
