// Debug filter for unread inbound messages
const messages = $input.all();
console.log('=== DEBUG FILTER START ===');
console.log('Total messages received:', messages.length);

const unreadMessages = [];

for (let i = 0; i < messages.length; i++) {
  const message = messages[i];
  console.log(`Message ${i}:`, {
    id: message.json.id,
    direction: message.json.direction,
    status: message.json.status,
    phone: message.json.phone_number
  });

  if (message.json.direction === 'inbound' && message.json.status === 'unread') {
    console.log('FOUND UNREAD INBOUND:', message.json.id);
    unreadMessages.push(message.json);
  }
}

console.log('Unread inbound messages found:', unreadMessages.length);
console.log('=== DEBUG FILTER END ===');

// Always return something, even if empty
if (unreadMessages.length === 0) {
  return [{ json: { debug: 'No unread messages found', total: messages.length } }];
}

return unreadMessages.map(msg => ({ json: msg }));
