// Debug: Extract messages from API response and filter for unread inbound
const apiResponse = $input.first().json;
console.log('=== DEBUG FILTER START ===');
console.log('API Response structure:', Object.keys(apiResponse));
console.log('Messages count:', apiResponse.messages ? apiResponse.messages.length : 'NO MESSAGES ARRAY');

const messages = apiResponse.messages || [];
console.log('Processing', messages.length, 'messages');

const unreadMessages = [];

for (let i = 0; i < messages.length; i++) {
  const message = messages[i];
  console.log(`Message ${i}:`, {
    id: message.id,
    direction: message.direction,
    status: message.status,
    phone: message.phone_number
  });

  if (message.direction === 'inbound' && message.status === 'unread') {
    console.log('FOUND UNREAD INBOUND:', message.id);
    unreadMessages.push(message);
  }
}

console.log('Unread inbound messages found:', unreadMessages.length);
console.log('=== DEBUG FILTER END ===');

// Return each unread message as a separate item
if (unreadMessages.length === 0) {
  return [{ json: { debug: 'No unread messages found', total: messages.length } }];
}

return unreadMessages.map(msg => ({ json: msg }));
